import React,{useEffect, useRef, useState} from 'react';
import {Line} from 'react-chartjs-2';
import {IChartData,IDatasetData} from '../../models/DataInterfaces';
import {IStockData,ISymbol} from '../../models/Data';
import {getChartStyle} from '../../styles/ChartStyles';
import {produceChartData} from '../../utility/ProcessData';

export function Chart({symbolData,tooltip}:{symbolData:IStockData,tooltip:boolean}){
    let [data,setData] = useState<IChartData|{}>({});
    useEffect(()=>{
        setData(produceChartData(symbolData));
    },[symbolData]);
    return (
        <div style={getChartStyle()}>
            <Line data={data} options={{maintainAspectRatio:false,tooltips:{enabled:tooltip}}} />
        </div>
    );   
}
