import React,{useEffect, useRef, useState} from 'react';

export function Chart(setSymbols:any){
    let cnv:any = useRef(null);
    useEffect(()=>{
        let ctx = cnv.current.getContext('2d');
        ctx.beginPath();
        ctx.moveTo(0,0);
        ctx.lineTo(100,200);
        ctx.stroke();
        // ctx.rect(40,40,200,200);
        // ctx.fillStyle = 'blue';
        // ctx.fill();
    },[]);
    return (
        <canvas ref={cnv} style={{marginLeft:'5%',marginTop:'6%',width:'50%',height:'50%',marginBottom:'5%',backgroundColor:'white'}}>
        </canvas>
    );   
}
