import React,{useEffect, useRef, useState} from 'react';
import {Line} from 'react-chartjs-2';
import {IChartData,IDatasetData} from '../../models/DataInterfaces';
import {IStockData,ISymbol} from '../../models/Data';
import {getChartStyle} from '../../styles/ChartStyles';

function produceIChartData(symbolData:IStockData):IChartData|any{
    if(symbolData.highest === undefined){
        return {};
    }
    const weekDays:Array<string> = ['Monday','Thursday','Wednesday','Tuesday','Friday'];
    let labels:Array<string> = [];
    for(let index:number = 0; index < symbolData.lowest.length; index++){
        labels.push(weekDays[index%5]);
    }
    let dataClose:IDatasetData = {label:'Average Prices',data:symbolData.close,fill:true,borderColor:'blue'};
    let dataHgh:IDatasetData = {label:'High Prices',data:symbolData.highest,fill:true,borderColor:'purple'};
    let dataLow:IDatasetData = {label:'Low Prices',data:symbolData.lowest,fill:true,borderColor:'red'};
    let chartData:IChartData = {labels:labels,datasets:[dataLow,dataClose,dataHgh]};
    return chartData;
}

export function Chart({symbolData,tooltip}:{symbolData:IStockData,tooltip:boolean}){
    let [data,setData] = useState<IChartData|{}>({});
    useEffect(()=>{
        setData(produceIChartData(symbolData));
    },[symbolData]);
    return (
        <div style={getChartStyle()}>
            <Line data={data} options={{maintainAspectRatio:false,tooltips:{enabled:tooltip}}}/>
        </div>
    );   
}
