import React,{useEffect, useRef, useState} from 'react';
import {Line} from 'react-chartjs-2';

function produceData():any{
    let data = {
        labels:["Jan","Feb","Mar","Apr","May","Jun"],
        height:'50%',
        datasets:[
            {
                label:'First dataset',
                data:[33,53,85,41,44,65],
                fill:true,
                backgroundColor:'rgba(75,192,192,0.2)',
                borderColor:'rgba(75,192,192,1)'
            },
            {
                label:'Second dataset',
                data:[33,25,35,51,54,76],
                fill:false,
                borderColor:'#742774'
            }
        ]
    };
    return data;
}

export function Chart(setSymbols:any){
    let chrt:any = useRef(null);
    useEffect(()=>{
        console.log(chrt.current.style.width);
    },[]);
    let data = produceData();
    return (
        <div ref={chrt} style={{position:'absolute',left:'5%',top:'10%',width:'90%',height:'85%',backgroundColor:'white',borderTopRightRadius:'2.5%',borderBottomLeftRadius:'2.5%'}}>
            {/* <svg viewBox="0 0 500 500">
                <polyline fill="none" stroke="#0074d9" strokeWidth="3" points="0,400
                200,150
                400,220"/>
            </svg> */}
            <Line data={data} options={{height:'50%'}}/>
        </div>
    );   
}
