import React,{useRef,useState,useEffect, Dispatch, SetStateAction} from 'react';
import TextField from '@material-ui/core/TextField';
import {RetrieveApiData} from '../utility/ApiData';
import CreateSearchResults from './SearchResults';
import { createImportSpecifier } from 'typescript';
import {ISymbol} from '../models/Data';
import {getSearchResultsContainerStyle,getSearchResultStyle} from '../styles/SearchStyle';


export function Search({setSymbolData}:{setSymbolData:Dispatch<SetStateAction<ISymbol>>}){
    let [symbols,setSymbols] = useState<Array<ISymbol>>([]);
    let [searchContent,setSearchContent] = useState<string>('');
    let [searchResults,setSearchResults] = useState<Array<ISymbol>>([]);
    useEffect(()=>{
        RetrieveApiData(setSymbols);
    },[]);
    useEffect(()=>{
        CreateSearchResults(symbols,searchContent,setSearchResults);
    },[searchContent]); 
    let whenFocus = (e:any):void=>{
        setIsFocused(true);
    };
    let whenBlur = (e:any):void=>{
        setTimeout(()=>{
            setIsFocused(false);
        },150);
        
    };
    let searchKeyUp = (e:any):void=>{
        setSearchContent(e.target.value);
    };
    let [isFocused,setIsFocused] = useState(false);
    let cont = <div style={getSearchResultsContainerStyle()}>{searchResults.map((el:ISymbol,i:number) => <div key={i} onClick={(e)=>{setSymbolData(el);}} style={getSearchResultStyle()}>{el.description}({el.notation})</div>)}</div>
    if(isFocused === true){
        return (
            <div>
                <div style={{marginLeft:'40%',marginTop:'1%',position:'relative'}}>
                    <TextField onFocus={whenFocus} onBlur={whenBlur} onKeyUp={searchKeyUp} style={{width:'35%',height:'10%'}} label='Search' type='search' variant='outlined'/> 
                    {cont}
                </div>
            </div>
        );
    }else{
        return (
            <div>
                <div style={{marginLeft:'40%',marginTop:'1%',position:'relative'}}>
                    <TextField onFocus={whenFocus} onBlur={whenBlur} style={{width:'35%',height:'10%'}} label='Search' type='search' variant='outlined'/>
                </div>
            </div>
        )
    }
}
