import React,{useState,useEffect} from 'react';
import {Chart} from './Chart';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import {RetrieveSymbolData} from '../utility/ApiData';
import { Button } from '@material-ui/core';
import {IChartData} from '../models/DataInterfaces';
import {IStockData} from '../models/Data';

export function ChartCont({ISymbol}:any){
    let [startDate,setStartDate] = useState<Date>(new Date());
    let [endDate,setEndDate] = useState<Date>(new Date());
    let [ISymbolData,setISymbolData] = useState<IStockData>({lowest:[],highest:[],close:[]});
    useEffect(()=>{
        if(ISymbol !== undefined){
            RetrieveISymbolData(setISymbolData,ISymbol,startDate,endDate);
        }
    },[ISymbol,startDate,endDate]);
    let [tooltip,setTooltip] = useState<boolean>(true);
    let [btnText,setBtnText] = useState<string>('Disable Tooltips');
    return (
        <div style={{left:'5%',position:'absolute',width:'90%',height:'80%',backgroundColor:'whitesmoke',borderRadius:'2%'}}>
            <div style={{marginLeft:'2%',marginTop:'0.5%',overflow:'auto',display:'flex'}}>
                <DatePicker selected={startDate} onChange={(date:Date) =>{ setStartDate(date);}}></DatePicker>
                <DatePicker selected={endDate} onChange={(date:Date) => {setEndDate(date);}}></DatePicker>
                <Button style={{marginLeft:'2%',backgroundColor:'dodgerblue'}} onClick={e=>{setTooltip(!tooltip);
                    if(btnText === 'Disable Tooltips'){
                        setBtnText('Enable Tooltips');
                    }else{
                        setBtnText('Disable Tooltips');
                    }
                }}>{btnText}</Button>
            </div>
            <Chart ISymbolData={ISymbolData} tooltip={tooltip}/>
        </div>
    );
}
